void destroy() {
  try {
    if (solrReq != null) {
      SolrDispatchFilter.log.debug("Closing out SolrRequest: {}", solrReq);
      solrReq.close();
    }
  } finally {
    try {
      if (core != null) core.close();
    } finally {
      SolrRequestInfo.clearRequestInfo();
    }
  }
}

