@Override
public long ramBytesUsed() {
  long mem = RamUsageEstimator.shallowSizeOf(this) + RamUsageEstimator.shallowSizeOf(relatives);
  for (TSTNode node : relatives) {
    if (node != null) {
      mem += node.ramBytesUsed();
    }
  }
  return mem;
}

