void toString(StringBuilder sb, int depth) {
  indent(sb, depth);
  if (left == null) {
    assert right == null;
    sb.append("leaf: " + start + " to " + end);
  } else {
    sb.append("node: " + start + " to " + end);
  }
  if (outputs != null) {
    sb.append(" outputs=");
    sb.append(outputs);
  }
  sb.append('\n');

  if (left != null) {
    assert right != null;
    left.toString(sb, depth+1);
    right.toString(sb, depth+1);
  }
}

