/**
 * Quote and escape input value for CSV
 * @param original
 */
public static String quoteEscape(String original) {
  String result = original;

  if (result.indexOf('\"') >= 0) {
    result.replace("\"", ESCAPED_QUOTE);
  }
  if(result.indexOf(COMMA) >= 0) {
    result = "\"" + result + "\"";
  }
  return result;
}

