public IndexOptions indexOptions() {
  if (!indexed()) {
    return IndexOptions.NONE;
  }

  IndexOptions options = IndexOptions.DOCS_AND_FREQS_AND_POSITIONS;
  if (omitTermFreqAndPositions()) {
    options = IndexOptions.DOCS;
