@Override
public synchronized void close() throws IOException {
  if (!isOpen) {
    // allow double close - usually to be consistent with other closeables
    return; // already closed
   }
  isOpen = false;
  if (writer != null) {
    assert openForWrite;
    writer.close();
  } else {
    IOUtils.close(handle);
  }
}

