/* Return byte[]'s to the pool */
@Override
void recycleByteBlocks(byte[][] blocks, int start, int end) {
  synchronized(DocumentsWriter.this) {
    for(int i=start;i<end;i++)
      freeByteBlocks.add(blocks[i]);
  }
}

