public long writeData(Object o) {
  LogCodec codec = new LogCodec();
  try {
    long pos = fos.size();   // if we had flushed, this should be equal to channel.position()
    codec.init(fos);
    codec.writeVal(o);
    return pos;
  } catch (IOException e) {
    throw new SolrException(SolrException.ErrorCode.SERVER_ERROR, e);
  }
}

