IndexDocValuesArray(int bytesPerValue, ValueType type) {
  this.bytesPerValue = bytesPerValue;
  this.type = type;
  switch (type) {
  case FIXED_INTS_16:
  case FIXED_INTS_32:
  case FIXED_INTS_64:
  case FIXED_INTS_8:
    isFloat = false;
    break;
  case FLOAT_32:
  case FLOAT_64:
    isFloat = true;
    break;
  default:
    throw new IllegalStateException("illegal type: " + type);

  }
}

