      accum += code;
    } else {
      accum += code >>> 1;            // shift off low bit
      if ((code & 1) != 0) {          // if low bit is set
        freq = 1;                     // freq is one
      } else {
        freq = freqIn.readVInt();     // else read freq
      }
    }

    if (accum >= target && (liveDocs == null || liveDocs.get(accum))) {
      return accum;
    }
  }

  return NO_MORE_DOCS;
