private final void advancePool(int docID) {
  long numBytes = (docID - (lastDocID+1))*size;
  while(numBytes > 0) {
    if (numBytes + pool.byteUpto < byteBlockSize) {
      pool.byteUpto += numBytes;
      numBytes = 0;
    } else {
      numBytes -= byteBlockSize - pool.byteUpto;
      pool.nextBuffer();
    }
  }
  assert numBytes == 0;
}

