static void writeFieldVal(String val, FieldType ft, Appendable out, int flags) throws IOException {
  if (ft!=null) {
    try {
      out.append(ft.indexedToReadable(val));
    } catch (Exception e) {
      out.append("EXCEPTION(val=");
      out.append(val);
      out.append(")");
    }
  } else {
    out.append(val);
  }
}

