private void buildSpellCheckIndex()
{
  lrf = h.getRequestFactory("spellchecker", 0, 20 );
  lrf.args.put("version","2.0");
  lrf.args.put("sp.query.accuracy",".9");

  assertU("Add some words to the Spell Check Index:",
      adoc("id",  "100",
           "spell", "solr"));
    assertU(adoc("id",  "101",
                 "spell", "cat"));
    assertU(adoc("id",  "102",
                 "spell", "cart"));
    assertU(adoc("id",  "103",
                 "spell", "carp"));
    assertU(adoc("id",  "104",
                 "spell", "cant"));
    assertU(adoc("id",  "105",
                 "spell", "catnip"));
    assertU(adoc("id",  "106",
                 "spell", "cattails"));
    assertU(adoc("id",  "107",
                 "spell", "cod"));
    assertU(adoc("id",  "108",
                 "spell", "corn"));
    assertU(adoc("id",  "109",
                 "spell", "cot"));

    assertU(commit());
    assertU(optimize());

    lrf.args.put("cmd","rebuild");
    assertQ("Need to first build the index:",
            req("cat")
            ,"//str[@name='cmdExecuted'][.='rebuild']"
            ,"//str[@name='words'][.='cat']"
            ,"//str[@name='exist'][.='true']"
    //        ,"//arr[@name='suggestions'][.='']"
            );
    lrf.args.clear();

}

