protected int readCharFromBuffer() throws IOException {
  if( charBufferIndex >= charBufferLen ){
    charBufferLen = input.read( charBuffer );
    if( charBufferLen == -1 ){
      return -1;
    }
    charBufferIndex = 0;
  }
  int c = (int)charBuffer[charBufferIndex++];
  nextStartOffset++;
  return c;
}

