private static Directory newFSDirectoryImpl(
    Class<? extends FSDirectory> clazz, File file)
    throws IOException {
  FSDirectory d = null;
  try {
    d = CommandLineUtil.newFSDirectory(clazz, file);
  } catch (Exception e) {
    d = FSDirectory.open(file);
  }
  return d;
}

