public Config(SolrResourceLoader loader, String name, Document doc) {
  this.prefix = null;
  this.doc = doc;
  try {
    this.origDoc = copyDoc(doc);
  } catch (TransformerException e) {
    throw new SolrException(SolrException.ErrorCode.SERVER_ERROR, e);
  }
  this.name = name;
  this.loader = loader;
}

