void downHeap(int size) {
  int i = 0;
  final DisiWrapper<Iter> node = heap[0];
  int j = leftNode(i);
  if (j < size) {
    int k = rightNode(j);
    if (k < size && heap[k].doc < heap[j].doc) {
      j = k;
    }
    if (heap[j].doc < node.doc) {
      do {
        heap[i] = heap[j];
        i = j;
        j = leftNode(i);
        k = rightNode(j);
        if (k < size && heap[k].doc < heap[j].doc) {
          j = k;
        }
      } while (j < size && heap[j].doc < node.doc);
      heap[i] = node;
    }
  }
}

