  this.configFile = configFile;
  InputStream in = new FileInputStream(configFile);
  try {
    this.load(dir, in, configFile.getName().endsWith(".xml"),  configFile.getName());
  } finally {
    IOUtils.closeQuietly(in);
  }
}

