private int getNumberOfReaders() {
  RefCounted<SolrIndexSearcher> searcherRef = h.getCore().getSearcher();
  try {
    SolrIndexSearcher searcher = searcherRef.get();
    return searcher.getTopReaderContext().leaves().size();
  } finally {
    searcherRef.decref();
  }
}

