@Override
public boolean incrementToken() throws IOException {
  clearAttributes();
  buffer.setLength(0);
  int ci;
  char ch, pch;
  boolean atBegin = true;
  tokenStart = tokenEnd;
  ci = input.read();
  ch = (char) ci;

  while (true) {
    if (ci == -1) {
      break;
    } else if (PUNCTION.indexOf(ch) != -1) {
      // End of a sentence
      buffer.append(ch);
      tokenEnd++;
      break;
    } else if (atBegin && Utility.SPACES.indexOf(ch) != -1) {
      tokenStart++;
      tokenEnd++;
      ci = input.read();
      ch = (char) ci;
    } else {
      buffer.append(ch);
      atBegin = false;
      tokenEnd++;
      pch = ch;
      ci = input.read();
      ch = (char) ci;
      // Two spaces, such as CR, LF
      if (Utility.SPACES.indexOf(ch) != -1
          && Utility.SPACES.indexOf(pch) != -1) {
        // buffer.append(ch);
        tokenEnd++;
        break;
      }
    }
  }
  if (buffer.length() == 0)
    return false;
  else {
    termAtt.setTermBuffer(buffer.toString());
    offsetAtt.setOffset(correctOffset(tokenStart), correctOffset(tokenEnd));
    typeAtt.setType("sentence");
    return true;
  }
}

