protected String getArg(String n, Map<String,String> args) {
  String s = args.remove(n);
  if (s == null) {
    throw new SolrException(SolrException.ErrorCode.SERVER_ERROR, "Mising parameter '"+n+"' for FieldType=" + typeName +args);
  }
  return s;
}

