          if (c == LOCALPARAM_END || Character.isWhitespace(c)) {
            val = p.val.substring(valStart, p.pos);
            break;
          }
          p.pos++;
        }
      }

      if (deref) {  // dereference parameter
        if (params != null) {
          val = params.get(val);
        }
      }
    }
    if (target != null) target.put(id, val);
  }
}

