public InputStream getData(String query) {
  File f = FileDataSource.getFile(basePath,query);
  try {
    return new FileInputStream(f);
  } catch (FileNotFoundException e) {
    wrapAndThrow(SEVERE,e,"Unable to open file "+f.getAbsolutePath());
    return null;
  }
}

