private void b_fixLeaf() {
  if (bytes[b_off + b_len - 1] == LEAF_BYTE) {
    b_len--;
    setLeaf();
  } else if (getLevel() == spatialPrefixTree.getMaxLevels()) {
    setLeaf();
  }
}

