    } catch (InterruptedException e) {
      log.warn("Thread interrupted while waiting for CDCR replicator threadpool close.");
      Thread.currentThread().interrupt();
    } finally {
      scheduler.shutdownNow();
      isStarted = false;
    }
  }
}

