/**
 * Tell all {@link SolrCoreAware} instances about the SolrCore
 */
public void inform(SolrCore core) 
{
  for( SolrCoreAware aware : waitingForCore ) {
    aware.inform( core );
  }
  waitingForCore.clear();
}

/**
 * Tell all {@link SolrCoreAware} instances about the SolrCore
 */
public void inform(SolrCore core) 
{
  this.dataDir = core.getDataDir();
  for( SolrCoreAware aware : waitingForCore ) {
    aware.inform( core );
  }
  waitingForCore.clear();
}

