private static DirectoryReader refreshReader(DirectoryReader reader) throws IOException {
  DirectoryReader oldReader = reader;
  reader = DirectoryReader.openIfChanged(reader);
  if (reader != null) {
    oldReader.close();
    return reader;
  } else {
    return oldReader;
  }
}

