public static String getHex( byte [] raw, int sz ) {
  final String HEXES = "0123456789ABCDEF";
  if ( raw == null ) {
    return null;
  }
  final StringBuilder hex = new StringBuilder( 2 * raw.length );
  for ( int i=0; i<sz; i++ ) {
    if (i>0 && (i)%16 == 0)
      hex.append("\n");
    byte b=raw[i];
    hex.append(HEXES.charAt((b & 0xF0) >> 4))
       .append(HEXES.charAt((b & 0x0F)))
       .append(" ");
  }
  return hex.toString();
}

