/** Stops all cores. */
public void shutdown() {
  synchronized(cores) {
    for( SolrCore core : cores.values() ) {
      core.close();
    }
    cores.clear();
  }
}

/** Stops all cores. */
public void shutdown() {
  synchronized(cores) {
    for(Map.Entry<String,CoreDescriptor> e : cores.entrySet()) {
      SolrCore core = e.getValue().getCore();
      if (core == null) continue;
      String key = e.getKey();
      if (core.getName().equals(key))
      core.close();
    }
    cores.clear();
  }
}

