public synchronized void blockUntilFinished()
{
  if( lock == null ) {
    lock = new ReentrantLock();
  }
  lock.lock();

  // Wait until no runners are running
  Runner runner = runners.peek();
  while( runner != null ) {
    runner.lock.lock();
    runner.lock.unlock();
    runner = runners.peek();
  }
  lock.unlock();
  lock = null;
}

public synchronized void blockUntilFinished()
{
  lock = new CountDownLatch(1);
  try {
    // Wait until no runners are running
    Runner runner = runners.peek();
    while( runner != null ) {
      runner.runnerLock.lock();
      runner.runnerLock.unlock();
      runner = runners.peek();
    }
  } finally {
    lock.countDown();
    lock=null;
  }
}

