    HttpSolrClient.Builder builder = new HttpSolrClient.Builder(host);
    if (httpClient != null) {
      builder = builder.withHttpClient(httpClient);
    }
    client = builder.build();
    solrClients.put(host, client);
  }
  return client;
}

