/**
 * @return a Collection of registered SolrCores
 */
public Collection<SolrCore> getCores() {
  java.util.List<SolrCore> l = new java.util.ArrayList<SolrCore>();
  synchronized (cores) {
    for(CoreDescriptor descr : this.cores.values()) {
      if (descr.getCore() != null)
        l.add(descr.getCore());
    }
  }
  return l;
}

