@Override
public void unmarkExclusiveTask(String collectionName, String operation) {
  if(!CLUSTERSTATUS.isEqual(operation) && collectionName != null) {
    synchronized (collectionWip) {
      collectionWip.remove(collectionName);
    }
  }
}

