public synchronized void setNorm(int doc, String field, byte value)
  throws IOException {
  Norm norm = (Norm)norms.get(field);
  if (norm == null)                             // not an indexed field
    return;
  norm.dirty = true;                            // mark it dirty
  normsDirty = true;

  norms(field)[doc] = value;                    // set the value
}

