/*
 * Test a deletion policy that keeps last N commits
 * around, with reader doing deletes.
 */
public void testKeepLastNDeletionPolicyWithReader() throws IOException {

  final int N = 10;

  for(int pass=0;pass<2;pass++) {

    boolean useCompoundFile = (pass % 2) != 0;

    KeepLastNDeletionPolicy policy = new KeepLastNDeletionPolicy(N);

    Directory dir = new RAMDirectory();
    IndexWriter writer = new IndexWriter(dir, new WhitespaceAnalyzer(TEST_VERSION_CURRENT), true, policy, IndexWriter.MaxFieldLength.UNLIMITED);
    writer.setUseCompoundFile(useCompoundFile);
    writer.close();
    Term searchTerm = new Term("content", "aaa");        
    Query query = new TermQuery(searchTerm);

    for(int i=0;i<N+1;i++) {
      writer = new IndexWriter(dir, new WhitespaceAnalyzer(TEST_VERSION_CURRENT), false, policy, IndexWriter.MaxFieldLength.UNLIMITED);
      writer.setUseCompoundFile(useCompoundFile);
      for(int j=0;j<17;j++) {
        addDoc(writer);
      }
      // this is a commit
      writer.close();
      IndexReader reader = IndexReader.open(dir, policy, false);
      reader.deleteDocument(3*i+1);
      reader.setNorm(4*i+1, "content", 2.0F);
      IndexSearcher searcher = new IndexSearcher(reader);
      ScoreDoc[] hits = searcher.search(query, null, 1000).scoreDocs;
      assertEquals(16*(1+i), hits.length);
      // this is a commit
      reader.close();
      searcher.close();
    }
    writer = new IndexWriter(dir, new WhitespaceAnalyzer(TEST_VERSION_CURRENT), false, policy, IndexWriter.MaxFieldLength.UNLIMITED);
    writer.setUseCompoundFile(useCompoundFile);
    writer.optimize();
    // this is a commit
    writer.close();

    assertEquals(2*(N+2), policy.numOnInit);
    assertEquals(2*(N+2)-1, policy.numOnCommit);

    IndexSearcher searcher = new IndexSearcher(dir, false);
    ScoreDoc[] hits = searcher.search(query, null, 1000).scoreDocs;
    assertEquals(176, hits.length);

    // Simplistic check: just verify only the past N segments_N's still
    // exist, and, I can open a reader on each:
    long gen = SegmentInfos.getCurrentSegmentGeneration(dir);

    dir.deleteFile(IndexFileNames.SEGMENTS_GEN);
    int expectedCount = 176;

    for(int i=0;i<N+1;i++) {
      try {
        IndexReader reader = IndexReader.open(dir, true);

        // Work backwards in commits on what the expected
        // count should be.
        searcher = new IndexSearcher(reader);
        hits = searcher.search(query, null, 1000).scoreDocs;
        if (i > 1) {
          if (i % 2 == 0) {
            expectedCount += 1;
          } else {
            expectedCount -= 17;
          }
        }
        assertEquals(expectedCount, hits.length);
        searcher.close();
        reader.close();
        if (i == N) {
          fail("should have failed on commits before last 5");
        }
      } catch (IOException e) {
        if (i != N) {
          throw e;
        }
      }
      if (i < N) {
        dir.deleteFile(IndexFileNames.fileNameFromGeneration(IndexFileNames.SEGMENTS, "", gen));
      }
      gen--;
    }

    dir.close();
  }
}

