@Override
public Properties getSolrProperties(ConfigSolr cfg, String context) {
  try {
    return readProperties(((NodeList) evaluate(
        context, XPathConstants.NODESET)).item(0));
  } catch (Throwable e) {
    SolrException.log(log, null, e);
  }
  return null;

}

