public static String getCharsetFromContentType( String contentType )
{
  if( contentType != null ) {
    int idx = contentType.toLowerCase().indexOf( "charset=" );
    if( idx > 0 ) {
      return contentType.substring( idx + "charset=".length() ).trim();
    }
  }
  return null;
}

