private ByteBuffer getFileContent(String f) throws IOException {
  ByteBuffer jar;
  try (FileInputStream fis = new FileInputStream(getFile(f))) {
    byte[] buf = new byte[fis.available()];
    fis.read(buf);
    jar = ByteBuffer.wrap(buf);
  }
  return jar;
}

