public void close() {
  for (Map.Entry<String, SolrRequestHandler> e : handlers.entrySet()) {
    if (e.getValue() instanceof AutoCloseable) {
      try {
        ((AutoCloseable) e.getValue()).close();
      } catch (Exception exp) {
        log.error("Error closing requestHandler "+e.getKey() , exp);
      }
    }

  }

}

