@Override
public void copy(int slot, int doc) {
  final int ord = (int) readerOrds.get(doc);
  if (ord == 0) {
    values[slot] = null;
  } else {
    ords[slot] = ord;
    assert ord > 0;
    if (values[slot] == null) {
      values[slot] = new BytesRef();
    }
    termsIndex.lookup(ord, values[slot]);
  }
  readerGen[slot] = currentReaderGen;
}

