@Override
public float computeNorm(String fieldName, FieldInvertState state) {
  final int numTokens;
  boolean overlaps = discountOverlaps;
  if (ln_overlaps.containsKey(fieldName)) {
    overlaps = ln_overlaps.get(fieldName).booleanValue();
