/**
 * Set up the writer to write at address.
 */ 
public void init(int address) {
  slice = pool.buffers[address >> DocumentsWriter.BYTE_BLOCK_SHIFT];
  assert slice != null;
  upto = address & DocumentsWriter.BYTE_BLOCK_MASK;
  offset0 = address;
  assert upto < slice.length;
}

