/**
 * Returns true for the next token in the stream, or false at EOS.
 * See http://java.sun.com/j2se/1.3/docs/api/java/lang/Character.UnicodeBlock.html
 * for detail.
 *
 * @return false for end of stream, true otherwise
 *
 * @throws java.io.IOException - throw IOException when read error <br>
 *         happened in the InputStream
 *
 */
@Override
public boolean incrementToken() throws IOException {
    clearAttributes();
    /** how many character(s) has been stored in buffer */

    while(true) { // loop until we find a non-empty token

      int length = 0;

      /** the position used to create Token */
      int start = offset;

      while (true) { // loop until we've found a full token
        /** current character */
        char c;

        /** unicode block of current character for detail */
        Character.UnicodeBlock ub;

        offset++;

        if (bufferIndex >= dataLen) {
            dataLen = input.read(ioBuffer);
            bufferIndex = 0;
        }

        if (dataLen == -1) {
            if (length > 0) {
                if (preIsTokened == true) {
                    length = 0;
                    preIsTokened = false;
                }
                else{
                  offset--;
                }

                break;
            } else {
                offset--;
                return false;
            }
        } else {
            //get current character
            c = ioBuffer[bufferIndex++];

            //get the UnicodeBlock of the current character
            ub = Character.UnicodeBlock.of(c);
        }

        //if the current character is ASCII or Extend ASCII
        if ((ub == Character.UnicodeBlock.BASIC_LATIN)
                || (ub == Character.UnicodeBlock.HALFWIDTH_AND_FULLWIDTH_FORMS)
           ) {
            if (ub == Character.UnicodeBlock.HALFWIDTH_AND_FULLWIDTH_FORMS) {
              int i = (int) c;
              if (i >= 65281 && i <= 65374) {
                // convert certain HALFWIDTH_AND_FULLWIDTH_FORMS to BASIC_LATIN
                i = i - 65248;
                c = (char) i;
              }
            }

            // if the current character is a letter or "_" "+" "#"
            if (Character.isLetterOrDigit(c)
                    || ((c == '_') || (c == '+') || (c == '#'))
               ) {
                if (length == 0) {
                    // "javaC1C2C3C4linux" <br>
                    //      ^--: the current character begin to token the ASCII
                    // letter
                    start = offset - 1;
                } else if (tokenType == DOUBLE_TOKEN_TYPE) {
                    // "javaC1C2C3C4linux" <br>
                    //              ^--: the previous non-ASCII
                    // : the current character
                    offset--;
                    bufferIndex--;

                    if (preIsTokened == true) {
                        // there is only one non-ASCII has been stored
                        length = 0;
                        preIsTokened = false;
                        break;
                    } else {
                        break;
                    }
                }

                // store the LowerCase(c) in the buffer
                buffer[length++] = Character.toLowerCase(c);
                tokenType = SINGLE_TOKEN_TYPE;

                // break the procedure if buffer overflowed!
                if (length == MAX_WORD_LEN) {
                    break;
                }
            } else if (length > 0) {
                if (preIsTokened == true) {
                    length = 0;
                    preIsTokened = false;
                } else {
                    break;
                }
            }
        } else {
            // non-ASCII letter, e.g."C1C2C3C4"
            if (Character.isLetter(c)) {
                if (length == 0) {
                    start = offset - 1;
                    buffer[length++] = c;
                    tokenType = DOUBLE_TOKEN_TYPE;
                } else {
                  if (tokenType == SINGLE_TOKEN_TYPE) {
                        offset--;
                        bufferIndex--;

                        //return the previous ASCII characters
                        break;
                    } else {
                        buffer[length++] = c;
                        tokenType = DOUBLE_TOKEN_TYPE;

                        if (length == 2) {
                            offset--;
                            bufferIndex--;
                            preIsTokened = true;

                            break;
                        }
                    }
                }
            } else if (length > 0) {
                if (preIsTokened == true) {
                    // empty the buffer
                    length = 0;
                    preIsTokened = false;
                } else {
                    break;
                }
            }
        }
    }

    if (length > 0) {
      termAtt.copyBuffer(buffer, 0, length);
      offsetAtt.setOffset(correctOffset(start), correctOffset(start+length));
      typeAtt.setType(TOKEN_TYPE_NAMES[tokenType]);
      return true;
    } else if (dataLen == -1) {
      offset--;
      return false;
    }

    // Cycle back and try for the next token (don't
    // return an empty string)
  }
}

