private void appendFilters(List<Filter> filters, String occurString, StringBuilder buffer) {
  if (filters != null) {
    for (Filter filter : filters) {
      buffer.append(' ');
      buffer.append(occurString);
      buffer.append(filter.toString());
    }
  }
}

