public Field(String name, BytesRef bytes, IndexableFieldType type) {
  if (type.indexed() && !type.tokenized()) {
    throw new IllegalArgumentException("Non-tokenized fields must use String values");
  }

  this.fieldsData = bytes;
  this.type = type;
  this.name = name;
}

