private int getIntFromContext(String prop, int ifNull) {
  int v = ifNull;
  try {
    String val = context.getEntityAttribute(prop);
    if (val != null) {
      val = context.replaceTokens(val);
      v = Integer.valueOf(val);
    }
  } catch (NumberFormatException e) {
    //do nothing
  }
  return v;
}

