public void testDontCreate() throws Throwable {
  File path = new File(System.getProperty("tempDir"), "doesnotexist");
  try {
    assertTrue(!path.exists());
    Directory dir = new FSDirectory(path, null);
    assertTrue(!path.exists());
    dir.close();
  } finally {
    _TestUtil.rmDir(path);
  }
}

