private String maxTimeStr(boolean multiThreaded) {
  String s =
    "( " +
    "2*resolution +  TIME_ALLOWED + SLOW_DOWN = " +
    "2*" + TimeLimitingCollector.getResolution() + " + " + TIME_ALLOWED + " + " + SLOW_DOWN +
    ")";
