  try (DirectoryStream<Path> stream = Files.newDirectoryStream(reutersDir, "*.sgm")) {
    for (Path sgmFile : stream) {
      extractFile(sgmFile);
      count++;
    }
  }
  if (count == 0) {
    System.err.println("No .sgm files in " + reutersDir);
  }
}

