public Query getQuery(String query, Analyzer a) throws Exception {
  if (a == null)
    a = new SimpleAnalyzer();
  QueryParser qp = new QueryParser("field", a);
  return qp.parse(query);
}

public Query getQuery(String query, Analyzer a) throws Exception {
  if (a == null)
    a = new SimpleAnalyzer();
  QueryParser qp = new QueryParser("field", a);
  qp.setOperator(QueryParser.DEFAULT_OPERATOR_OR);
  return qp.parse(query);
}

